import './App.css';
import React, { useState } from 'react';

function App() {
  const [todos, setTodos] = useState([]); // Initialize state to manage the list of todos
  const [inputValue, setInputValue] = useState(''); // State to manage the input value

  const addTodoList = () => {
    if (inputValue.trim() !== '') {
      setTodos([...todos, inputValue]); // Add new todo to the list
      setInputValue(''); // Clear the input after adding

    }else{
      alert("입력해주세요")
    }

  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value); // Update input value as the user types
  };

  const deleteTodoList = (index) => {
    todos.splice(index, 1);
    setTodos([...todos]);
  };

  const checkTodoList = (index, event) => {
    const isChecked = event.target.checked;

    // 새로운 todos 배열을 생성하고, 필요한 항목만 업데이트합니다.
    const updatedTodos = todos.map((todo, i) => 
      i === index ? { ...todo, checked: isChecked } : todo
    );

    setTodos(updatedTodos);
  };


  return (
    <div className="App">
      <input
        className="input"
        value={inputValue}
        onChange={handleInputChange}
      />
      <button type="button" onClick={addTodoList}>추가</button>

      <div>
        {todos.map((todo, index) => (
          <li key={index} style = {{ color: todo.checked ? 'red' : 'black'}}><input checked={todo.checked} type="checkbox" onChange={(event) => checkTodoList(index, event)}></input>{todo}<button type="button" onClick={() =>deleteTodoList(index)}>삭제</button></li>
        ))}
      </div>

    </div>
  );
}

export default App;
