import './App.css';
import React, { useState } from 'react';

function App() {
  const [todos, setTodos] = useState([]); // Initialize state to manage the list of todos
  const [inputValue, setInputValue] = useState(''); // State to manage the input value

  const addTodoList = () => {
    if (inputValue.trim() !== '') {
      setTodos([...todos, inputValue]); // Add new todo to the list
      setInputValue(''); // Clear the input after adding

    }else{
      alert("입력해주세요")
    }

  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value); // Update input value as the user types
  };

  const DeleteTodoList = ({todo}) => {
    todos.pop(todo);
    setTodos([...todos]);
  };

  return (
    <div className="App">
      <input
        className="input"
        value={inputValue}
        onChange={handleInputChange}
      />
      <button type="button" onClick={addTodoList}>추가</button>
      {/* <button type="button" onClick={DeleteTodoList}>삭제</button> */}

      <div>
        {todos.map((todo, index) => (
          <li key={index}>{todo}<button type="button" onClick={DeleteTodoList({todo})}>삭제</button></li>
        ))}
      </div>

    </div>
  );
}

export default App;
