import React, { useState } from 'react';

const App = () => {
  const [todos, setTodos] = useState([]);
  const [inputValue, setInputValue] = useState('');

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const addTodoList = () => {
    if (inputValue.trim()) {
      setTodos([...todos, { text: inputValue, style: { color: 'black' }, checked: false }]);
      setInputValue('');
    }
  };

  const checkTodoList = (index, event) => {
    const isChecked = event.target.checked;

    // 새로운 todos 배열을 생성하여 상태를 업데이트합니다.
    const updatedTodos = todos.map((todo, i) =>
      i === index
        ? { ...todo, checked: isChecked, style: { textDecoration : isChecked ? 'line-through' : 'none'} }
        : todo
    );

    setTodos(updatedTodos);
  };

  const deleteTodoList = (index) => {
    // 특정 인덱스의 항목을 제외한 새로운 todos 배열을 생성하여 상태를 업데이트합니다.
    const updatedTodos = todos.filter((_, i) => i !== index);
    setTodos(updatedTodos);
  };

  return (
    <div className="App">
      <input
        className="input"
        value={inputValue}
        onChange={handleInputChange}
      />
      <button type="button" onClick={addTodoList}>추가</button>

      <ul>
        {todos.map((todo, index) => (
          <li key={index} style={todo.style}>
            <input
              type="checkbox"
              checked={todo.checked}
              onChange={(event) => checkTodoList(index, event)}
            />
            {todo.text}
            <button type="button" onClick={() => deleteTodoList(index)}>삭제</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
