import './App.css';
import React, { useState } from 'react';

function App() {
  const [todos, setTodos] = useState([]); // Initialize state to manage the list of todos
  const [inputValue, setInputValue] = useState(''); // State to manage the input value

  function addTodoList() {
    setTodos([...todos, inputValue]);
    setInputValue('');
  }

  return (
    <div className="App">
      <input
        className="input"
        value={inputValue}
      />
      <button type="button" onClick={addTodoList}>추가</button>


    </div>
  );
}

export default App;
